/**
 * Creates a trigger object used to create internal physics objects that interact with rigid bodies
 * and trigger collision events with no collision response.
 *
 * @ignore
 */
export class Trigger {
    /**
     * Create a new Trigger instance.
     *
     * @param {import('../../app-base.js').AppBase} app - The running {@link AppBase}.
     * @param {import('../component.js').Component} component - The component for which the trigger
     * will be created.
     * @param {ComponentData} data - The data for the component.
     */
    constructor(app: import('../../app-base.js').AppBase, component: import('../component.js').Component, data: ComponentData);
    entity: import("../../entity.js").Entity;
    component: import("../component.js").Component;
    app: import("../../app-base.js").AppBase;
    initialize(data: any): void;
    body: any;
    destroy(): void;
    _getEntityTransform(transform: any): void;
    updateTransform(): void;
    enable(): void;
    disable(): void;
}
