/**
 * Returns program library for a specified instance of a device.
 *
 * @param {import('../../platform/graphics/graphics-device.js').GraphicsDevice} device - The
 * graphics device used to own the program library.
 * @returns {import('./program-library.js').ProgramLibrary} The instance of {@link ProgramLibrary}
 * @ignore
 */
export function getProgramLibrary(device: import('../../platform/graphics/graphics-device.js').GraphicsDevice): import('./program-library.js').ProgramLibrary;
/**
 * Assigns the program library to device cache.
 *
 * @param {import('../../platform/graphics/graphics-device.js').GraphicsDevice} device - The
 * graphics device used to own the program library.
 * @param {import('./program-library.js').ProgramLibrary} library - The instance of
 * {@link ProgramLibrary}.
 * @ignore
 */
export function setProgramLibrary(device: import('../../platform/graphics/graphics-device.js').GraphicsDevice, library: import('./program-library.js').ProgramLibrary): void;
